package com.thecodeguy.libraryapp;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;

import java.util.ArrayList;

public class AllBooksActivity extends AppCompatActivity {
    private RecyclerView booksRecyclerView;
    private BookRecViewAdapter adapter;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_all_books);
        adapter = new BookRecViewAdapter(this);
        booksRecyclerView = findViewById(R.id.books_recycler_view);
        booksRecyclerView.setAdapter(adapter);
        booksRecyclerView.setLayoutManager(new GridLayoutManager(this,2));
        ArrayList<Book> books = new ArrayList<>();
        books.add(new Book(1,"Harry Potter and the Sorcerer's Stone","J.K. Rowling",320,"data:image/jpeg;base64,","Harry Potter and the Sorcerer's Stone is the first novel in the Harry Potter series and Rowling's debut novel. It was published in 1997 by Bloomsbury.","Harry Potter and the Sorcerer's Stone is the first novel in the Harry Potter series and Rowling's debut novel. It was published in 1997 by Bloomsbury."));
        adapter.setBooks(books);

    }
}